import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./Comment.css";
import Api from "../Common/Api";

const SongCommentPage = () => {
    const [songInfo, setSongInfo] = useState({});
    const [comments, setComments] = useState([]);
    const { id } = useParams();
    const getApi = Api();

    useEffect(() => {
        const getSongInfo = () => {
            axios
                .get(getApi.concat("/song/") + id)
                .then((res) => {
                    setSongInfo(res.data);
                })
                .catch((err) => {
                    console.log(err);
                });
        };

        const getComments = () => {
            axios
                .get(getApi.concat("/comments/") + id)
                .then((res) => {
                    setComments(res.data);
                })
                .catch((err) => {
                    console.log(err);
                });
        };

        getSongInfo();
        getComments();
    }, [getApi, id]);

    return (
        <div className="song-comment-page mt-5">
            {/* 노래 정보 표시 */}
            <div className="song-info">
                <img
                    src={songInfo.image}
                    alt={songInfo.title}
                    className="album-image"
                />
                <h2>{songInfo.title}</h2>
                <h4>by {songInfo.artist}</h4>
                <p>Rank: {songInfo.rank}</p>
            </div>

            {/* 댓글 목록 표시 */}
            <div className="comments-section">
                <h3>Comments</h3>
                <table className="table table-bordered">
                    <thead>
                        <tr>
                            <th>User</th>
                            <th>Title</th>
                            <th>Comment</th>
                            <th>Date</th>
                            <th>Stars</th>
                        </tr>
                    </thead>
                    <tbody>
                        {comments.map((comment) => (
                            <tr key={comment.id}>
                                <td>{comment.user}</td>
                                <td>{comment.title}</td>
                                <td>{comment.comm}</td>
                                <td>{comment.date}</td>
                                <td>{comment.star}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default SongCommentPage;
