import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./Comment.css";
import Api from "../Common/Api";

const SongCommentPage = () => {
    const [songInfo, setSongInfo] = useState({});
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState({
        user: "",
        pw: "",
        star: 0,
        title: "",
        comm: "",
    });
    const { title } = useParams();
    const getSongApi = Api(1);
    const getCommentsApi = Api();

    useEffect(() => {
        const getSongInfo = () => {
            axios
                .get(getSongApi)
                .then((res) => {
                    const song = res.data.find((song) => song.title === title);
                    if (song) {
                        setSongInfo(song);
                    } else {
                        console.log("Song not found");
                    }
                })
                .catch((err) => {
                    console.log(err);
                });
        };

        const getComments = () => {
            axios
                .get(getCommentsApi)
                .then((res) => {
                    const filteredComments = res.data.filter(
                        (comment) => comment.song === title
                    );
                    setComments(filteredComments);
                })
                .catch((err) => {
                    console.log(err);
                });
        };

        getSongInfo();
        getComments();
    }, [getSongApi, getCommentsApi, title]);

    const handleInputChange = (e) => {
        const { name, value, type } = e.target;
        const newValue = type === "radio" ? parseInt(value) : value;
        setNewComment({ ...newComment, [name]: newValue });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // 여기에서 댓글을 추가하는 API 호출을 할 수 있습니다.
        console.log(newComment);
    };

    return (
        <div className="song-comment-page mt-5">
            {/* 노래 정보 표시 */}
            <div className="song-info">
                <img
                    src={songInfo.image}
                    alt={songInfo.title}
                    className="album-image"
                    style={{ width: "100px", height: "100px" }}
                />
                <h2>{songInfo.title}</h2>
                <h4>by {songInfo.artist}</h4>
                <p>Rank: {songInfo.rank}</p>
            </div>

            {/* 댓글 작성 폼 */}
            <div className="comment-form">
                <h3>Write a Comment</h3>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label>Username:</label>
                        <input
                            type="text"
                            name="user"
                            value={newComment.user}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label>Password:</label>
                        <input
                            type="password"
                            name="pw"
                            value={newComment.pw}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <div class="wrap">
                            <h1>Star rating</h1>
                            <div class="rating">
                                <label
                                    class="rating__label rating__label--half"
                                    for="starhalf"
                                >
                                    <input
                                        type="radio"
                                        id="starhalf"
                                        class="rating__input"
                                        name="rating"
                                        value=""
                                    />
                                    <span class="star-icon"></span>
                                </label>
                                <label
                                    class="rating__label rating__label--full"
                                    for="star1"
                                >
                                    <input
                                        type="radio"
                                        id="star1"
                                        class="rating__input"
                                        name="rating"
                                        value=""
                                    />
                                    <span class="star-icon"></span>
                                </label>
                                <label
                                    class="rating__label rating__label--half"
                                    for="star1half"
                                >
                                    <input
                                        type="radio"
                                        id="star1half"
                                        class="rating__input"
                                        name="rating"
                                        value=""
                                    />
                                    <span class="star-icon"></span>
                                </label>
                                <label
                                    class="rating__label rating__label--full"
                                    for="star2"
                                >
                                    <input
                                        type="radio"
                                        id="star2"
                                        class="rating__input"
                                        name="rating"
                                        value=""
                                    />
                                    <span class="star-icon"></span>
                                </label>
                                <label
                                    class="rating__label rating__label--half"
                                    for="star2half"
                                >
                                    <input
                                        type="radio"
                                        id="star2half"
                                        class="rating__input"
                                        name="rating"
                                        value=""
                                    />
                                    <span class="star-icon"></span>
                                </label>
                                <label
                                    class="rating__label rating__label--full"
                                    for="star3"
                                >
                                    <input
                                        type="radio"
                                        id="star3"
                                        class="rating__input"
                                        name="rating"
                                        value=""
                                    />
                                    <span class="star-icon"></span>
                                </label>
                                <label
                                    class="rating__label rating__label--half"
                                    for="star3half"
                                >
                                    <input
                                        type="radio"
                                        id="star3half"
                                        class="rating__input"
                                        name="rating"
                                        defaultChecked
                                    />
                                    <span class="star-icon"></span>
                                </label>
                                <label
                                    class="rating__label rating__label--full"
                                    for="star4"
                                >
                                    <input
                                        type="radio"
                                        id="star4"
                                        class="rating__input"
                                        name="rating"
                                        value=""
                                    />
                                    <span class="star-icon"></span>
                                </label>
                                <label
                                    class="rating__label rating__label--half"
                                    for="star4half"
                                >
                                    <input
                                        type="radio"
                                        id="star4half"
                                        class="rating__input"
                                        name="rating"
                                        value=""
                                    />
                                    <span class="star-icon"></span>
                                </label>
                                <label
                                    class="rating__label rating__label--full"
                                    for="star5"
                                >
                                    <input
                                        type="radio"
                                        id="star5"
                                        class="rating__input"
                                        name="rating"
                                        value=""
                                    />
                                    <span class="star-icon"></span>
                                </label>
                            </div>
                        </div>
                        <label>Star Rating:</label>
                        {[1, 2, 3, 4, 5].map((star) => (
                            <label key={star}>
                                <input
                                    type="radio"
                                    name="star"
                                    value={star}
                                    checked={newComment.star === star}
                                    onChange={handleInputChange}
                                />
                                {star}
                            </label>
                        ))}
                    </div>
                    <div className="form-group">
                        <label>Title:</label>
                        <input
                            type="text"
                            name="title"
                            value={newComment.title}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label>Comment:</label>
                        <textarea
                            name="comm"
                            value={newComment.comm}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <button type="submit">Submit Comment</button>
                </form>
            </div>

            {/* 댓글 목록 표시 */}
            <div className="comments-section">
                <h3>Comments</h3>
                {comments.map((comment) => (
                    <div key={comment.id} className="comment-item">
                        <div className="comment-header">
                            <span className="comment-user">{comment.user}</span>
                            <span className="comment-time">{comment.date}</span>
                        </div>
                        <div className="comment-body">
                            <p className="comment-title">{comment.title}</p>
                            <p className="comment-text">{comment.comm}</p>
                        </div>
                        <div className="comment-footer">
                            <span className="comment-stars">
                                ⭐ {comment.star}
                            </span>
                        </div>
                    </div>
                ))}
            </div>
            <script src="./Star.js"></script>
        </div>
    );
};

export default SongCommentPage;
