import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./Comment.scss";
import Api from "../Common/Api";

const EditCommentPage = () => {
    const { id } = useParams();
    const [comment, setComment] = useState({
        user: "",
        pw: "",
        star: 0,
        title: "",
        comm: "",
    });
    const getCommentApi = `${Api()}/${id}`;

    useEffect(() => {
        axios
            .get(getCommentApi)
            .then((res) => {
                setComment(res.data);
            })
            .catch((err) => {
                console.error("Error fetching comment:", err);
            });
    }, [getCommentApi]);

    const handleInputChange = (e) => {
        const { name, value, type } = e.target;
        const newValue = type === "radio" ? parseFloat(value) : value;
        setComment({ ...comment, [name]: newValue });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios
            .put(getCommentApi, comment)
            .then(() => {
                alert("Comment updated successfully!");
                // Optionally redirect or perform other actions
            })
            .catch((err) => {
                console.error("Error updating comment:", err);
            });
    };

    return (
        <div className="edit-comment-page">
            <h3>Edit Comment</h3>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Username:</label>
                    <input
                        type="text"
                        name="user"
                        value={comment.user}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Password:</label>
                    <input
                        type="password"
                        name="pw"
                        value={comment.pw}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Rating:</label>
                    <div className="rating">
                        {[0.5, 1, 1.5, 2, 2.5, 3, 3.5, 4, 4.5, 5].map(
                            (value) => (
                                <label key={value}>
                                    <input
                                        type="radio"
                                        name="star"
                                        value={value}
                                        checked={comment.star === value}
                                        onChange={handleInputChange}
                                    />
                                    {value} ⭐
                                </label>
                            )
                        )}
                    </div>
                </div>
                <div className="form-group">
                    <label>Title:</label>
                    <input
                        type="text"
                        name="title"
                        value={comment.title}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Comment:</label>
                    <textarea
                        name="comm"
                        value={comment.comm}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <button type="submit">Update Comment</button>
            </form>
        </div>
    );
};

export default EditCommentPage;
