import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Loader from "../Common/Loader";
import "./User.css";
const CreateUser_temp = () => {
    const navigate = useNavigate();
    const createUserApi =
        "https://672881f2270bd0b97555cd4e.mockapi.io/api/crud";
    const [error, setError] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [user, setUser] = useState({
        id: "",
        userID: "",
        title: "",
        body: "",
    });

    const handleInput = (event) => {
        event.preventDefault();
        const { name, value } = event.target;
        console.log(name, value);
        setUser({ ...user, [name]: value });
    };

    const handleSubmit = async (event) => {
        var cid = 1;
        event.preventDefault();
        console.log(user);
        try {
            setIsLoading(true);
            const response1 = await fetch(createUserApi, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(""),
            }).then(function (response) {
                return response.json();
            });

            if (response1.ok) {
                console.log(JSON.parse(response1));
                return;
                // for (key in JSON.parse(response1)) {
                //     var resID = parseInt(res[key].id);
                //     if (resID >= cid) {
                //         cid = resID + 1;
                //     }
                // }
                setUser({ ...user, ["id"]: cid });
            } else {
                console.error("Form getter failed!");
                return;
            }

            const response2 = await fetch(createUserApi, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(user),
            });

            if (response2.ok) {
                console.log("Form submitted successfully!");
                setUser({
                    id: "",
                    userID: "",
                    title: "",
                    body: "",
                });
                navigate("/show-user");
            } else {
                console.error("Form submission failed!");
            }
        } catch (error) {
            setError(error.message);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="user-form">
            <div className="heading">
                {isLoading && <Loader />}
                {error && <p>Error: {error}</p>}
                <p>User Form</p>
            </div>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="userID" className="form-label">
                        user ID
                    </label>
                    <input
                        type="text"
                        className="form-control"
                        id="userID"
                        name="userID"
                        value={user.userID}
                        onChange={handleInput}
                    />
                </div>
                <div className="mb-3 mt-3">
                    <label htmlFor="title" className="form-label">
                        Title
                    </label>
                    <input
                        type="text"
                        className="form-control"
                        id="title"
                        name="title"
                        value={user.title}
                        onChange={handleInput}
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="body" className="form-label">
                        Comment
                    </label>
                    <input
                        type="text"
                        className="form-control"
                        id="body"
                        name="body"
                        value={user.body}
                        onChange={handleInput}
                    />
                </div>
                <button type="submit" className="btn btn-primary submit-btn">
                    Submit
                </button>
            </form>
        </div>
    );
};

export default CreateUser_temp;
